def sort_addr(a, b):
    if a.lower() < b.lower():
        return a, b
    else:
        return b, a


def align_tick(tick, spacing):
    return tick // spacing * spacing


class V3LP:
    def __init__(
        self,
        cli: Web3Client,
        token_0,
        token_1,
        token_0_min_amount,
        token_1_min_amount,
        factory_addr="0x1a3c9B1d2F0529D97f2afC5136Cc23e58f1FD35B",
        nft_manager_addr="0x00c7f3082833e796A5b3e4Bd59f6642FF44DCD15",
    ) -> None:
        self.token0, self.token1 = token_0, token_1
        self.token_0_min_amount, self.token_1_min_amount = (
            token_0_min_amount,
            token_1_min_amount,
        )
        self.cli = cli
        self.nft_manager = cli.eth.contract(nft_manager_addr, abi=nft_manager_abi)
        self.factory = cli.eth.contract(factory_addr, abi=factory_abi)
        pool_addr = self.factory.functions["poolByPair"](token_0, token_1).call()
        self.pool = cli.eth.contract(pool_addr, abi=pool_abi)
        self.tick_spacing = self.pool.functions["tickSpacing"]().call()
        self.last_add_ts = time.time()

    def balanceOf(self):
        return self.nft_manager.functions["balanceOf"](self.cli.acc.address).call()

    def current_tick(self):
        return (
            self.pool.functions["globalState"]().call()[1]
            // self.tick_spacing
            * self.tick_spacing
        )

    def position_info(self, token_id):
        return self.nft_manager.functions["positions"](token_id).call()

    def position_liquidity(self, liq):
        return liq[6]

    def position_ticks(self, liq):
        return liq[4], liq[5]

    def add_liquidity(self, current_tick, liqs):
        logging.info(f"尝试添加流动性 {liqs}")
        if (
            liqs[0]["amount0"] < self.token_0_min_amount
            or liqs[1]["amount1"] < self.token_1_min_amount
        ):
            raise Exception("not enough token")

        data = []
        for i in liqs:
            data.append(
                self.nft_manager.encodeABI(
                    "mint",
                    [
                        {
                            "token0": self.token0,
                            "token1": self.token1,
                            "tickLower": i["tick_lower"],
                            "tickUpper": i["tick_upper"],
                            "amount0Desired": i["amount0"],
                            "amount1Desired": i["amount1"],
                            "amount0Min": 0,
                            "amount1Min": 0,
                            "recipient": self.cli.acc.address,
                            "deadline": int(time.time()) + 1000,
                        }
                    ],
                )
            )

        result = (
            self.nft_manager.functions["multicall"](data)
            .transact({"gas": 3000000})
            .hex()
        )
        send_notify(
            f"""INFO: 添加流动性 ✅
tx: {result}
current tick: {current_tick}
{json.dumps(liqs, indent=2)}
"""
        )
        self.last_add_ts = time.time()
        return result

    def remove_liquidity(self, token_ids):
        logging.info(f"尝试移除流动性 {token_ids}")
        data = []
        for token_id in token_ids:
            pos = self.position_info(token_id)
            lp = self.position_liquidity(pos)
            dec = self._decrease_liquidity(token_id, lp)
            col = self._collect(token_id)
            bn = self._burn(token_id)
            data.append(dec)
            data.append(col)
            data.append(bn)
        result = (
            self.nft_manager.functions["multicall"](data)
            .transact({"gas": 3000000})
            .hex()
        )
        send_notify(
            f"""INFO: 移除流动性 ✅
tx: {result}
{json.dumps(token_ids, indent=2)}
"""
        )
        return result

    def _get_token_id(self, index):
        return self.nft_manager.functions["tokenOfOwnerByIndex"](
            self.cli.acc.address, index
        ).call()

    def get_token_ids(self):
        return [self._get_token_id(i) for i in range(self.balanceOf())]

    def _collect(self, token_id):
        return self.nft_manager.encodeABI(
            "collect",
            [
                {
                    "tokenId": token_id,
                    "recipient": self.cli.acc.address,
                    "amount0Max": 2**128 - 1,
                    "amount1Max": 2**128 - 1,
                }
            ],
        )

    def _decrease_liquidity(self, token_id, liquidity):
        return self.nft_manager.encodeABI(
            "decreaseLiquidity",
            [
                {
                    "tokenId": token_id,
                    "liquidity": liquidity,
                    "amount0Min": 0,
                    "amount1Min": 0,
                    "deadline": int(time.time()) + 1000,
                }
            ],
        )

    def _burn(self, token_id):
        return self.nft_manager.encodeABI("burn", [token_id])
